package core_java_basics;

public class Cast_Numberical_Values {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte by = 12;
		short sh = 123;
		int a = 1122;
		long b = 1122334;

		float c = 2.3f;
		double d = 2.3;

		d = a;
		System.out.println(d);

		a = (int) c;
		System.out.println(a);
	}

}
